import React, { useState, useEffect } from 'react';
import './App.css';
import { Button, Form, FormGroup, Label, Input, Alert } from 'reactstrap';
import logo from './assests/logo.jpg';

function App() {

  const [Name, setName] = useState('');
  const [Phone, setPhone] = useState('');

  const [isLoginView, setIsLoginView] = useState(true);
  const [alertView, setAlertView] = useState(false);

  const loginClicked = () => {
  };

  const registerClicked = () => {
  };


  return (
    <Form className="login-form">
      {isLoginView ?
        <div>
          <img src={logo} height="150" width="150" />
          <FormGroup>
            <Input type="text" name="Phone" id="Phone" placeholder="Phone" value={Phone}
              onChange={evt => setPhone(evt.target.value)} />
          </FormGroup>
          <Button color="green" className="btn-lg btn-block" onClick={loginClicked}>
            Login
                </Button>
          <span><p>or</p></span>
          <p style={{ color: "green" }} onClick={() => setIsLoginView(false)}>Create new account</p>
        </div>
        :
        <div>
          <img src={logo} height="150" width="150" />
          <FormGroup>
            <Input type="text" name="Phone" id="Phone" placeholder="Phone" value={Phone}
              onChange={evt => setPhone(evt.target.value)} />
          </FormGroup>
          <FormGroup>
            <Input
              type="text"
              name="Name"
              id="Name"
              placeholder="Name"
              value={Name}
              onChange={(evt) => setName(evt.target.value)}
            />
          </FormGroup>
          <Button color="success" type="button" className="btn-lg btn-block" onClick={registerClicked}>
            Register
                </Button>
          {alertView ?
            <Alert color="success">
              Now you are good to go to login page. Click Login here!
                            </Alert>
            : <Alert color="danger">
              All fields are required
                      </Alert>}


          <span><p>or</p></span>
          <p style={{ color: "green" }} onClick={() => setIsLoginView(true)}>Login here!</p>
        </div>}
    </Form >
  );
}

export default App;
