import React, { useState, useEffect } from 'react';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    NavbarText
} from 'reactstrap';
import logo from './assests/logo.jpg';
import { Alert } from 'reactstrap';
import { useCookies } from "react-cookie";
import axios from "axios";

const Home = () => {
    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [times, setTimes] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    const [token, setToken, deleteToken] = useCookies(["mr-token"]);

    const logoutClicked = () => {
        deleteToken(["mr-token"]);
    }
    var num = 5 - parseInt({ times })
    useEffect(() => {

        axios
            .post("/visited", {}, {
                headers: { Authorization: `Bearer ${token["mr-token"]}` },
            })
            .then((response) => {
                console.log('home')
                console.log(response)
                setName(response.data.updated_user.Name);
                setPhone(response.data.updated_user.Phone);
                setTimes(response.data.updated_user.Visited);
            })
            .catch((error) => {
                console.log(error);
            });

        if (!token["mr-token"]) window.location.href = "/";
    }, [token]);

    const toggle = () => setIsOpen(!isOpen);

    return (
        <>
            <div>
                <Navbar color="light" light expand="md">
                    <NavbarBrand style={{ color: "green" }} href="#"><img
                        src={logo}
                        width="30"
                        height="30"
                        alt="insideHome"
                    />Barber</NavbarBrand>
                    <NavbarToggler onClick={toggle} />
                    <Collapse isOpen={isOpen} navbar>
                        <Nav className="mr-auto" navbar>
                            <NavItem>
                                <NavLink style={{ color: "green" }} href="#">About and Contacts</NavLink>
                            </NavItem>
                        </Nav>
                        <NavbarText style={{ color: "red" }} onClick={logoutClicked}>Logout</NavbarText>
                    </Collapse>
                </Navbar>
            </div>
            <div>
                <Alert style={{ fontSize: '50px' }} color="primary">
                    {5 - parseInt(times)} more number of hair cut is needed to reach 5.
      </Alert>
            </div>
            <div>
                <h1>Name: {name}</h1>
                <h1>Phone: {phone}</h1>
                <h1>Visited times: {times}</h1>
            </div>
        </>
    );
}

export default Home;
